extensionAttribute.scriptContentsMac:
#!/bin/sh ## Script to detect and enumerate attached iLok Serial
SERIAL=`system_profiler SPUSBDataType | grep -B 5 -i ': iLok' | grep 'Serial' | cut -f 2 -d ":" | sed -e "s/ //"` #SERIAL=`system_profiler SPUSBDataType | grep -B 5 -i ': iLok' | grep 'Serial'`
echo ''$SERIAL''
